
public class LogicalOperations1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//! = not
		//&& = and
		//or = ||
		boolean p =true;
		boolean negation =!p;
		System.out.println("logical negation of p being "+p+" is "+negation);
		p=false;
		negation=!p;
		System.out.println("logical negation of p being "+p+" is "+negation);

	}

}
